import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';

class App extends Component {
  constructor(){
    super();
    this.state = { data: [] };
  }

  async loadPlayers(){
    let formData = new FormData();
    formData.append('queryMethod', 'getAllPlayers');
    const resp = await fetch('http://localhost:80/StirlandsDAP/strilandscricket/src/api/dbInterface.php', { mode: 'no-cors', method: 'POST', body: JSON.stringify({queryMethod: 'getAllPlayers'})}).then(response => response.json()).then(data => console.log(data));
    return resp.json();
  }

  async componentDidMount(){
    await this.loadPlayers();
  }
  
  render() {
    return (
      <div className="App">
        <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
          <p>
            Edit <code>src/App.js</code> and save to reload.
          </p>

          <a
            className="App-link"
            href="https://reactjs.org"
            target="_blank"
            rel="noopener noreferrer"
          >
            Learn React
          </a>
        </header>
      </div>
    );
  }
}
export default App;
